package UD05;

public class Ejercicio7 {

	public Ejercicio7() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*Aplicación para mostrar todos los números del 1 al 100, ambos incluidos
		 * Utilizar un bucle while
		 */
		
		//Declaro e inicializo la variable de contador
		int i = 1;
		
		//Abro el bucle con la condición de que mientras el contador sea menor o igual que 100, imprima y se siga incrementando
		while(i <= 100) {
			System.out.println(i);
			i++;
		}
		
	}

}
